function GetTermsTranslation($alias) {
	
	$lang_code = GetCleanLanguageCode();
	$terms_translations = GetTermsTranslationsArray();
	$key = $lang_code.'_'.$alias;
	
	if (array_key_exists($key, $terms_translations)) {
		return $terms_translations[$key];
	}
	
	return $key;
}

