<?php function print_iframe($url, $style, $height, $width, $settings){	echo '<iframe id="iframe" ';	echo 'src="'.$url.'" ';	echo ' style="'.$style.'" '; 	echo ' height="'.$height.'" '; 	echo ' width='.$width.'" ';	echo $settings;	echo ">";	echo "</iframe>";}function print_iframe_id($url, $style, $height, $width, $settings, $id){	echo '<iframe ';	echo ' id="' . $id . '" ';		echo ' src="'.$url.'" '; 	echo ' style="'.$style.'" '; 	echo ' width='.$width.'" ';	echo $settings;	echo ">";	echo "</iframe>";}?>